package basics;

public class exerciseRecursion {
    public static void main(String[] args) {
        T t = new T();
        int i = t.racursionNum(4);
        System.out.println(i);

    }

}

class T{
    public int racursionNum(int n1) {
        if (n1 == 1 || n1 == 2) {
            return 1;
        } else {
            return racursionNum(n1 - 1) + racursionNum(n1 - 2);
        }
    }
}

