    public static void bubble(Integer[] arr,Comparator c){
        int temp;
        for (int i = 0; i < arr.length-1 ; i++) {
            for (int j = 0; j <arr.length-1-i; j++) {
                if (c.compare(arr[j],arr[j+1])>0){
                    temp =arr[j];
                    arr[j] = arr[j+1];
                    arr[j+1] = temp;
                }
            }
        }
    }
